import React from "react";
import { withAsyncAction } from "../HOCs";
import { connect } from "react-redux";

class deleteMessage extends React.Component {
    deleteMessage = event => {
    event.preventDefault();
    this.props.delete.forEach(like => {
      if (like.username === this.props.username) {
        this.props.deleteMessage(this.props.token);
      }
    });
  };

  render() {
    return <button onClick={this.deleteMessage}>Delete!</button>;
  }
}

const mapStateToProps = state => {
  return {
    token: state.auth.login.result.token,
    username: state.auth.login.result.username
  };
};

export default connect(mapStateToProps)(
  withAsyncAction(deleteMessage)
);
