import React from "react";
import { withAsyncAction } from "../HOCs";
import "./CreateUser.css";
import { Input, Button } from "semantic-ui-react";
import { Link } from "react-router-dom";

class CreateUser extends React.Component {
  state = {
    username: "",
    password: "",
    displayName: "",
    message: ""
  };

  createUser = () => {
    const { username, password, displayName } = this.state;
    const userData = {
      username,
      password,
      displayName
    };

    fetch("https://kwitter-api.herokuapp.com/user", {
      method: "POST",
      headers: jsonHeaders,
      body: JSON.stringify(userData)
    })
      .then(response => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then(data => {
        //this.setState({ isLoading: false, downlines: data.response });
        console.log("New User Created: ", data.response.user.username);
      })
      .catch(error => {
        console.log("error: ", data.response.message);
        this.setState({ message: data.response.message });
      });
  };

  handleCreateUser = e => {
    e.preventDefault();
    this.createUser();
    console.log("handleCreateUser");
  };

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { username, password, displayName } = this.state;
    console.log(this.state);
    return (
      <React.Fragment>
        <div>
          <form id="register-form" onSubmit={this.handleLogin}>
            <h1>Register</h1>
            <Input
              size="huge"
              label="Username"
              type="text"
              name="username"
              placeholder="Enter your Name"
              autoFocus
              onChange={this.handleChange}
              value={username}
            />
            <Input
              size="huge"
              label="Password"
              type="password"
              name="password"
              placeholder="Enter your Password"
              onChange={this.handleChange}
              value={password}
            />
            <Input
              size="huge"
              label="Display Name"
              type="text"
              name="displayName"
              placeholder="Enter your full name"
              onChange={this.handleChange}
              value={displayName}
            />
            <Button
              style={{ color: "#5A4576", marginTop: "1px" }}
              size="huge"
              type="submit"
              onClick={this.handleCreateUser}
            >
              Register
            </Button>
            <Link
              style={{ marginLeft: "70px" }}
              to="/"
              className="buttonSizeCreateUser"
            >
              Go Home
            </Link>
          </form>
          <div id="formStyle"></div>
        </div>
      </React.Fragment>
    );
  }
}

export default withAsyncAction("auth", "login")(CreateUser);
