import React from "react";
import { withAsyncAction } from "../HOCs";
import "./CreateUser.css";
import { Input, Button } from "semantic-ui-react";
import { Link } from "react-router-dom";

const jsonHeaders = {
  "Content-Type": "application/json",
  Accept: "application/json"
};

class CreateUser extends React.Component {
  state = {
    username: "",
    password: "",
    displayName: "",
    hasValidUsername: true,
    hasValidPassword: true,
    hasValidDisplayName: true
  };

  createUser = () => {
    const { username, password, displayName } = this.state;
    const userData = {
      username,
      password,
      displayName
    };
    fetch("https://kwitter-api.herokuapp.com/users", {
      method: "POST",
      headers: jsonHeaders,
      body: JSON.stringify(userData)
    })
      .then(response => {
        if (!response.ok) throw new Error(response.status);
        else return response.json();
      })
      .then(data => {
        //this.setState({ isLoading: false, downlines: data.response });
        console.log("New User Created: ", data.user.username);
      })
      .catch(error => {
        console.log("error: ", error);
        //this.setState({ requestFailed: true });
      });
  };

  handleCreateUser = async e => {

    e.preventDefault();
    if (this.validate()) {
      this.createUser();
    }
  };

  validate = () => {
    const { username, password, displayName } = this.state;
    if (username.trim().length == 0) {
      this.setState({
        hasValidUsername: false
      });
      return false;
    } else {
      this.setState({
        hasValidUsername: true
      });
    }
    if (password.trim().length == 0) {
      this.setState({
        hasValidPassword: false
      });
      return false;
    } else {
      this.setState({
        hasValidPassword: true
      });
    }
    if (displayName.trim().length == 0) {
      this.setState({
        hasValidDisplayName: false
      });
      return false;
    } else {
      this.setState({
        hasValidDisplayName: true
      });
    }
    return true;
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  render() {
    const {
      username,
      password,
      displayName,
      hasValidUsername,
      hasValidPassword,
      hasValidDisplayName
    } = this.state;
    return (
      <React.Fragment>
        <div>
          <form id="register-form" onSubmit={this.handleLogin}>
            <h1>Register</h1>
            <div>
              <Input
                size="huge"
                label="Username"
                type="text"
                name="username"
                placeholder="Enter your Name"
                autoFocus
                onChange={this.handleChange}
                value={username}
                error={!hasValidUsername}
              />
            </div>
            <Input
              size="huge"
              label="Password"
              type="password"
              name="password"
              placeholder="Enter your Password"
              onChange={this.handleChange}
              value={password}
              error={!hasValidPassword}
            />
            <Input
              size="huge"
              label="Display Name"
              type="text"
              name="displayName"
              placeholder="Enter your full name"
              onChange={this.handleChange}
              value={displayName}
              error={!hasValidDisplayName}
            />
            <Button
              style={{ color: "#5A4576", marginTop: "1px" }}
              size="huge"
              type="submit"
              onClick={this.handleCreateUser}
            >
              Register
            </Button>

            <Link
              style={{ marginLeft: "70px" }}
              to="/"
              className="buttonSizeCreateUser"
            >
              Go Home
            </Link>
          </form>
          <div id="formStyle"></div>
        </div>
      </React.Fragment>
    );
  }
}

export default withAsyncAction("auth", "login")(CreateUser);
